mapper.select:
select distinct(method_name)
from operate_log
where class_name=#{classname};

select id, bin_to_uuid(operate_user) as operate_user, operate_time, class_name, method_name, method_params, return_value, cost_time from operate_log
class_name = #{className}
and method_name = #{methodName}
and operate_time between #{begin} and #{end}
ORDER BY operate_time DESC ,id DESC

select distinct(method_name)
from select_log
where class_name=#{classname};

select id, bin_to_uuid(operate_user) as operate_user, operate_time, class_name, method_name, method_params, cost_time from select_log
class_name = #{className}
and method_name = #{methodName}
and operate_time between #{begin} and #{end}
ORDER BY operate_time DESC ,id DESC
