mapper.update:
update tb_user
password=#{password}, name=#{name}, email=#{email}, gender=#{gender}, fail_count=#{failCount}, update_time=#{updateTime}, last_login_time=#{lastLoginTime}
where id= uuid_to_bin(#{id})

mapper.select:
select bin_to_uuid(id) as id,
username,
name,
gender,
email,
permission,
fail_count,
create_time,
last_login_time
from tb_user


        id = UUID_TO_BIN(#{id})


        and username = #{username}


        and email = #{email}


        and create_time between #{createTimeBegin}and #{createTimeEnd}


        and last_login_time between #{lastLoginTimeBegin}and #{lastLoginTimeEnd}

    and gender = #{gender}
    and permission = #{permission}
    and fail_count = #{failCount}

        and name = #{name}
